import "./App.css";
import { ethers } from "ethers";
import { useEffect, useState } from "react";
import abi from "./abi/abi.json";

function App() {
  const [account, setAccount] = useState(null);
  const [provider, setProvider] = useState(null);
  const [balance, setBalance] = useState(0);
  const getBalance = async () => {
    const tempBalance = await provider.getBalance("ethers.eth");
    setBalance(tempBalance);
  };

  const initConnection = async () => {
    if (typeof window.ethereum !== "undefined") {
      console.log("good");
      /*const accounts = await window.ethereum.request({
        method: "eth_requestAccounts",
      });*/
      const tempProvider = new ethers.providers.Web3Provider(window.ethereum);
      //setProvider(tempProvider);
      console.log(tempProvider);

      await provider.send("eth_requestAccounts", []);
      const signer = provider.getSigner();
      setAccount(signer.account);
      console.log(account);
    } else {
      console.log("install metamask");
    }
  };
  useEffect(() => {
    initConnection();
    //getBalance();
  }, []);

  return (
    <div className="page">
      <div className="header">
        <img
          src={require("./assets/images/logocryptobeast.gif")}
          className="logo"
        />
        <p>{balance}</p>
        {account == null ? (
          <button onClick={initConnection} className="button">
            Connect
          </button>
        ) : (
          <p>...{account.substring(account.length - 7)}</p>
        )}
      </div>
    </div>
  );
}

export default App;
