import "./App.css";
import { ethers } from "ethers";
import { useEffect, useState } from "react";
import abi from "./abi/abi.json";

function App() {
  const [account, setAccount] = useState(null);
  const [provider, setProvider] = useState(null);
  const [tickets, setTickets] = useState(0);

  const Ntickets = async () => {
    //await new Promise((r) => setTimeout(r, 2000));
    const contract = new ethers.Contract(
      "0xd034139f8ebC342e3b60b11dbBd104364b6c2980",
      abi,
      provider
    );
    const temTickets = await contract.s_randomWords(1);
    console.log(temTickets.toString());
    setTickets(temTickets);
  };

  const initConnection = async () => {
    if (typeof window.ethereum !== "undefined") {
      console.log("good");
      const accounts = await window.ethereum.request({
        method: "eth_requestAccounts",
      });
      const tempProvider = new ethers.providers.Web3Provider(window.ethereum);
      setProvider(tempProvider);
      console.log(provider);
      setAccount(accounts[0]);
    } else {
      console.log("install metamask");
    }
  };

  useEffect(() => {
    initConnection();
    Ntickets();
    //initConnection();
  }, []);

  return (
    <div className="page">
      <div className="header">
        <img
          src={require("./assets/images/logocryptobeast.gif")}
          className="logo"
        />
        <p>{tickets.toString()}</p>
        {account == null ? (
          <button onClick={initConnection} className="button">
            Connect
          </button>
        ) : (
          <p>...{account.substring(account.length - 7)}</p>
        )}
      </div>
      <button onClick={Ntickets().then(() => {})} className="button">
        Prueba
      </button>
    </div>
  );
}

export default App;
